import { Component, OnInit } from '@angular/core';
import { LceQuery } from '../interfaces/lcequery';
import { Qlpic101Service } from '../services/qlpic101.service';
import { McQuery } from '../interfaces/mc-query';

@Component({
  selector: 'app-learn',
  templateUrl: './learn.component.html',
  styleUrls: ['./learn.component.css']
})
export class LearnComponent {
  qlpic101alla: LceQuery[] = []
  qArray: LceQuery[] = []
  aArray: McQuery[] = []

  showanswers = false
  queryNrAnswersShow = -1



  constructor(
    private qlpic101all: Qlpic101Service
  ) {
    this.qlpic101alla = this.qlpic101all.getallAll()
    // this.qArray = this.qlpic101all.getallAll()
    // console.log(this.qlpic101alla)
    for (let i = 0; i < this.qlpic101alla.length; i++) {
      this.qArray.push(this.qlpic101alla[i])
    }
  }

  toggleAnswers(qid: number): void {
    if (this.queryNrAnswersShow != qid) {
      this.queryNrAnswersShow = qid;
      this.showanswers = true
    } else {
      this.showanswers = !this.showanswers
    }
  }
}
<h2 style="text-align: center">Learning All Questions</h2>

<div *ngFor="let query of qArray">
  <h5>Question: {{ query.qid }} <br /></h5>
  <div *ngFor="let qt of query.qtxt">
    <span *ngIf="qt[0] === ' '"> _ {{ qt }} <br /> </span>
    <span *ngIf="qt[0] != ' '"> {{ qt }} <br /> </span>
  </div>
  <div *ngFor="let ans of query.qanswers; index as k">
    <span *ngIf="showanswers">
      <span *ngIf="ans.correct">
        <span *ngIf="queryNrAnswersShow === query.qid" class="boldgreen">
          {{ k + 1 }}: {{ ans.txt }}
        </span>
      </span>
      <span *ngIf="!ans.correct">
        <span *ngIf="queryNrAnswersShow === query.qid">
          {{ k + 1 }}: {{ ans.txt }}
        </span>
      </span>
      <span *ngIf="queryNrAnswersShow != query.qid">
        {{ k + 1 }}: {{ ans.txt }}
      </span>
  </span>
    <span *ngIf="!showanswers"> {{ k + 1 }}: {{ ans.txt }} </span>
  </div>
  <button type="button" class="btn btn-outline-primary" style="margin-left: 80%" (click)="toggleAnswers(query.qid)">check</button>
  <hr />
</div>
