import { Component, OnInit } from '@angular/core';
import { McQuery } from '../interfaces/mc-query';
import { Statistics } from '../interfaces/statistics';
import { Qlpic101Service } from '../services/qlpic101.service';
// import { StatsService } from '../services/stats.service';
import { ScQuery } from '../interfaces/sc-query';
import { FiQuery } from '../interfaces/fi-query';
import { LceQuery } from '../interfaces/lcequery';
@Component({
  selector: 'app-exam',
  templateUrl: './exam.component.html',
  styleUrls: ['./exam.component.css']
})
export class ExamComponent  {
  ql101all: LceQuery[] = []
  question: LceQuery
  currentQuestion: number=-1


  constructor(private qs: Qlpic101Service){
    this.ql101all=this.qs.getallAll();
    // // this.ql101all.map(q=>{q.qtyp=='mc';q.qanswers.map(a=>a.givenans=false)})
    // // this.ql101all.map(q=>{q.qtyp=='sc';q.qanswers.map(a=>a.givenans=false)})
    // this.ql101all.map(q=>{q.qtyp=='fi';q.qgiventxt=''})
    this.currentQuestion=0
    this.question=this.ql101all[this.currentQuestion]



  }
  nextQuestion() {
    if (this.currentQuestion < this.ql101all.length - 1) {
      this.currentQuestion++;
      this.question = this.ql101all[this.currentQuestion];
      this.resetGivenAnswers();
    } else {
      // Tüm soruları geçtiğimizde yapılacak işlemler burada olabilir.
      // Örneğin, sonuçları değerlendirme veya başka bir işlem yapma.
      console.log("Tüm soruları tamamladınız!");
    }
  }

  resetGivenAnswers() {
    this.question.qanswers.forEach(answer => {
      answer.givenans = false;
    });
    this.question.qgiventxt = '';
  }



}
