export class ExamComponent {
  constructor(ql101Mc, stats) {
    this.ql101Mc = ql101Mc;
    this.stats = stats;
    this.ql101all = [];
    this.showanswers = false;
    this.queryNrAnswersShow = -1;
    this.currentQnr = -1;
    this.control = true;
    this.selectedQuestionCount = 1;
    this.selectedQuestionType = 'all';
    this.quizStarted = false;
    this.correctInput = false;
    // maxexamwrong in %
    this.maxexamwrong = 20;
    this.examresult = false;
    this.showResultQuestions = false;
    this.ql101all = this.ql101Mc.getallAll();
    this.ql101Mc.initGivenAnswers();
    this.statistic = this.stats.calcStatsAll();
    this.currentQnr = 0;
    this.query = this.ql101all[this.currentQnr];
    this.gotolearnmode = false;
    this.examwrong = 0;
  }
  startQuiz() {
    this.control = false;
    this.quizStarted = true;
    this.currentQnr = 0;
    this.filterQuestions();
  }
  filterQuestions() {
    if (this.selectedQuestionType === 'all') {
      this.ql101all = this.ql101Mc.getallAll();
    } else if (this.selectedQuestionType === 'mc') {
      this.ql101all = this.ql101Mc.getallMc();
    } else if (this.selectedQuestionType === 'fi') {
      this.ql101all = this.ql101Mc.getallFi();
    } else if (this.selectedQuestionType === 'sc') {
      this.ql101all = this.ql101Mc.getallSc();
    }
    this.ql101all = this.ql101all.slice(0, this.selectedQuestionCount);
  }
  shuffleArray(array) {
    for (let i = array.length - 1; i > 0; i--) {
      const j = Math.floor(Math.random() * (i + 1));
      [array[i], array[j]] = [array[j], array[i]];
    }
    return array;
  }
  keyinput(myinput) {
    this.correctInput = false;
    this.query.qgiventxt = myinput;
    // check if correct
    if (this.query.qanswers.find(a => a.txt.find(t => t === this.query.qgiventxt))) {
      this.correctInput = true;
    }
    console.log(this.correctInput, this.query.qgiventxt);
    this.refreshStats();
  }
  changeGivenAnswer(ansind) {
    // first reset all radios
    this.query.qanswers.map(ans => ans.givenans = false);
    // second set new radio
    this.query.qanswers[ansind].givenans = !this.query.qanswers[ansind].givenans;
    this.refreshStats();
  }
  resetStats() {
    this.statistic = this.stats.resetStatsMc();
    this.firstQuery();
    this.gotolearnmode = false;
    this.examwrong = 0;
  }
  resetAnswers() {
    // reset 'givenanswer's of all questions
    this.ql101all.map(q => q.qanswers.map(a => a.givenans = false));
    this.resetStats();
    this.firstQuery();
    this.examwrong = 0;
  }
  refreshStats() {
    this.statistic = this.stats.calcStatsMc();
  }
  firstQuery() {
    this.currentQnr = 0;
    this.query = this.ql101all[this.currentQnr];
    this.showanswers = false;
    this.refreshStats();
  }
  prevQuery() {
    if (0 < this.currentQnr) {
      this.currentQnr--;
      this.query = this.ql101all[this.currentQnr];
    }
    this.showanswers = false;
    this.refreshStats();
  }
  nextQuery() {
    // ------------------------------------------------------
    // check if current question answered and correct -> next
    //   else update examwrong counter
    // answered?
    if (this.checkQueryAnswered()) {
      this.gotolearnmode = false;
      if (!this.checkQueryAnsweredCorrect()) {
        // answered and false -> PopUp and prev question
        // examwrong + 1 (max 7)
        this.examwrong++;
        // reset answers of current query - not in exam mode
        // this.query.qanswers.map(a => a.givenans = false)
        // back one query or to first query
        // this.prevQuery()
        this.refreshStats();
        console.log('Learn wrong: ', this.examwrong);
        console.log(this.examwrong, this.ql101all.length, this.maxexamwrong);
        if (100 * (this.examwrong / this.ql101all.length) > this.maxexamwrong) {
          // x% wrong, this is bad :( - popup and go to learn mode
          this.gotolearnmode = true;
        }
        // this.setNextQuestion()
      } else {// answered and true
        // to next question
        // this.setNextQuestion()
        // }
      }
    } else {
      // not answered -> next question
