export class CheckComponent {
  constructor(ql101srvc, stats) {
    this.ql101srvc = ql101srvc;
    this.stats = stats;
    this.ql101all = [];
    this.lArray = [];
    this.correctQuestions = [];
    this.incorrectQuestions = [];
    this.unansweredQuestions = [];
    this.showanswers = false;
    this.queryNrAnswersShow = -1;
    this.currentQnr = 0;
    this.correctInput = false;
    this.finish = false;
    this.maxlearnwrong = 7;
    this.showWrongAnswerWarning = false;
    this.control = true;
    this.quizStarted = false;
    this.selectedQuestionType = 'all';
    this.ql101all = this.ql101srvc.getallAll();
    for (let i = 0; i < this.ql101all.length; i++) {
      this.lArray.push(this.ql101all[i]);
    }
    this.currentQnr = 0;
    this.query = this.lArray[this.currentQnr];
    this.gotolearnmode = false;
    this.learnwrong = 0;
    this.learncorrect = 0;
    this.learnunanswered = 0;
  }
  ngOnInit() {
    this.ql101all = this.ql101srvc.getallAll();
    this.lArray = [...this.ql101all];
    this.query = this.lArray[this.currentQnr];
  }
  startQuiz() {
    this.control = false;
    this.quizStarted = true;
    this.currentQnr = 0;
    this.filterQuestions();
  }
  filterQuestions() {
    if (this.selectedQuestionType === 'all') {
      this.ql101all = this.ql101srvc.getallAll();
    } else if (this.selectedQuestionType === 'mc') {
      this.ql101all = this.ql101srvc.getallMc();
    } else if (this.selectedQuestionType === 'fi') {
      this.ql101all = this.ql101srvc.getallFi();
    } else if (this.selectedQuestionType === 'sc') {
      this.ql101all = this.ql101srvc.getallSc();
    }
  }
  refreshPage() {
    location.reload(); // Sayfayı yenile
  }

  firstQuery() {
    this.currentQnr = 0;
    this.query = this.ql101all[this.currentQnr];
    this.showanswers = false;
  }
  prevQuery() {
    if (0 < this.currentQnr) {
      this.currentQnr--;
      this.query = this.ql101all[this.currentQnr];
    }
    this.showanswers = false;
  }
  nextQuery() {
    // check if current question answered and correct -> next
    //   else update learnwrong counter
    if (this.query.qtyp === 'mc') {
      if (this.checkQueryMcAnswered()) {
        this.gotolearnmode = false;
        if (!this.checkQueryMcAnsweredCorrect()) {
          // answered and false -> PopUp and prev question
          // learnwrong + 1 (max 7)
          this.learnwrong++;
          // reset answers of current query
          this.query.qanswers.map(a => a.givenans = false);
          // this.query.qgiventxt = ''
          // back one query or to first query
          this.prevQuery();
          this.showWrongAnswerWarning = true;
          // 2 saniye sonra uyarı mesajını gizle
          setTimeout(() => {
            this.showWrongAnswerWarning = false;
          }, 1000);
          // this.refreshStats()
          console.log('Learn wrong: ', this.learnwrong);
          if (this.learnwrong >= this.maxlearnwrong) {
            // 7 wrong, this is bad :( - popup and go to learn mode
            this.gotolearnmode = true;
          }
        } else {
          // answered and true
          // to next question
          this.learncorrect++;
          this.setNextQuestion();
        }
      } else {
        // not answered -> next question
        this.learnunanswered++;
        this.setNextQuestion();
      }
    } else if (this.query.qtyp === 'sc') {
