import { Component, OnInit} from '@angular/core';
import { LceQuery } from '../interfaces/lcequery';
import { Qlpic101Service } from '../services/qlpic101.service';

@Component({
  selector: 'app-learn2',
  templateUrl: './learn2.component.html',
  styleUrls: ['./learn2.component.css']
})
export class Learn2Component {
  ql101msall: LceQuery[] = []

  showanswers = false
  queryNrAnswersShow = -1

  query: LceQuery;
  currentQnr = -1

  constructor(
    private ql101mc: Qlpic101Service
  ) {
    this.ql101msall = this.ql101mc.getallAll()

    this.currentQnr = 0
    this.query = this.ql101msall[this.currentQnr]
  }

  firstQuery() {
      this.currentQnr = 0
      this.query = this.ql101msall[this.currentQnr]
  }

  prevQuery() {
    if (0 < this.currentQnr) {
      this.currentQnr--
      this.query = this.ql101msall[this.currentQnr]
    }
  }

  nextQuery() {
    if (this.currentQnr < this.ql101msall.length - 1) {
      this.currentQnr++
      this.query = this.ql101msall[this.currentQnr]
      console.log(this.currentQnr, this.query)
    }
  }

  lastQuery() {
    this.currentQnr = this.ql101msall.length - 1
    this.query = this.ql101msall[this.currentQnr]
}


  toggleAnswers(qid: number): void {
    if (this.queryNrAnswersShow != qid) {
      this.queryNrAnswersShow = qid;
      this.showanswers = true
    } else {
      this.showanswers = !this.showanswers
    }
  }
}
<h2 style="text-align: center">Learning One Question</h2>

<button type="button" class="btn btn-outline-dark" (click)="firstQuery()">first</button>

<button type="button" class="btn btn-outline-secondary" style="margin-left: 80%
;" (click)="lastQuery()">last</button>

<div class="question-container"><h5>Question: {{ query.qid }} <br /></h5>
  <span *ngFor="let qtext of ql101msall[currentQnr].qtxt; let i = index">
    {{ qtext }}<span *ngIf="i !== ql101msall[currentQnr].qtxt.length - 1"> </span>
  </span>
  <div *ngFor="let ans of query.qanswers; index as k">
    <span *ngIf="showanswers">
      <span *ngIf="ans.correct">
        <span *ngIf="queryNrAnswersShow === query.qid" class="boldgreen">
          {{ k + 1 }}: {{ ans.txt }}
        </span>
      </span>
      <span *ngIf="!ans.correct">
        <span *ngIf="queryNrAnswersShow === query.qid">
          {{ k + 1 }}: {{ ans.txt }}
        </span>
      </span>
      <span *ngIf="queryNrAnswersShow != query.qid">
        {{ k + 1 }}: {{ ans.txt }}
      </span>
    </span>
    <span *ngIf="!showanswers"> {{ k + 1 }}: {{ ans.txt }} </span>
  </div></div>
<button type="button" class="btn btn-outline-success" style="margin-left: 18%
" (click)="prevQuery()">back</button>
<button type="button" class="btn btn-outline-primary" style="margin-left: 15%
" (click)="toggleAnswers(query.qid)">check</button>
<button type="button" class="btn btn-outline-success" style="margin-left: 15%
" (click)="nextQuery()">next</button>
