import { Component, OnInit } from '@angular/core';
import { Statistics } from '../interfaces/statistics';
import { Qlpic101Service } from '../services/qlpic101.service';
import { StatsService } from '../services/stats.service';
import { LceQuery } from '../interfaces/lcequery';

@Component({
  selector: 'app-check',
  templateUrl: './check.component.html',
  styleUrls: ['./check.component.css']
})
export class CheckComponent implements OnInit{
  ql101all: LceQuery[] = []
  lArray: LceQuery[] = []
  correctQuestions: LceQuery[] = [];
  incorrectQuestions: LceQuery[] = [];
  unansweredQuestions: LceQuery[] = [];


  showanswers = false
  queryNrAnswersShow = -1

  query: LceQuery;
  currentQnr = 0;
  correctInput: boolean = false;


  gotolearnmode: boolean
  finish: boolean=false
  learnwrong: number
  learncorrect: number
  learnunanswered: number
  maxlearnwrong = 7
  showWrongAnswerWarning = false;
  control: boolean = true;
  quizStarted: boolean = false;
  selectedQuestionType: string = 'all'



  constructor(
    private ql101srvc: Qlpic101Service,
    private stats: StatsService
  ) {
    this.ql101all = this.ql101srvc.getallAll()
    for (let i = 0; i < this.ql101all.length; i++) {
      this.lArray.push(this.ql101all[i])}

    this.currentQnr = 0
    this.query = this.lArray[this.currentQnr]
    this.gotolearnmode = false
    this.learnwrong = 0
    this.learncorrect=0
    this.learnunanswered=0

  }
  ngOnInit(): void { this.ql101all = this.ql101srvc.getallAll();
    this.lArray = [...this.ql101all];
    this.query = this.lArray[this.currentQnr];
  }
  startQuiz() {this.control = false
    this.filterQuestions();
    this.quizStarted = true;
    this.currentQnr = 0;
  }
  filterQuestions(){if (this.selectedQuestionType === 'all') {
    this.ql101all = this.ql101srvc.getallAll()
  } else if (this.selectedQuestionType === 'mc') {
    this.ql101all = this.ql101srvc.getallMc()
  }
  else if (this.selectedQuestionType === 'fi') {
    this.ql101all = this.ql101srvc.getallFi()
  }
  else if (this.selectedQuestionType === 'sc') {
    this.ql101all = this.ql101srvc.getallSc()
  }

  }


  refreshPage() {
    location.reload(); // Sayfayı yenile
  }


  firstQuery() {
    this.currentQnr = 0
    this.query = this.ql101all[this.currentQnr]
    this.showanswers = false

  }



  prevQuery() {
    if (0 < this.currentQnr) {
      this.currentQnr--
      this.query = this.ql101all[this.currentQnr]
    }
    this.showanswers = false

  }

  nextQuery() {
    // check if current question answered and correct -> next
    //   else update learnwrong counter
    if (this.query.qtyp === 'mc') {
      if (this.checkQueryMcAnswered()) {
        this.gotolearnmode = false        
        if (!this.checkQueryMcAnsweredCorrect()) {
          // answered and false -> PopUp and prev question
          // learnwrong + 1 (max 7)
          this.learnwrong++
          // reset answers of current query
          this.query.qanswers.map(a => a.givenans = false)
          // this.query.qgiventxt = ''
          // back one query or to first query
          this.prevQuery()
          this.showWrongAnswerWarning = true;

          // 2 saniye sonra uyarı mesajını gizle
          setTimeout(() => {
            this.showWrongAnswerWarning = false;
          }, 1000);
          // this.refreshStats()
          console.log('Learn wrong: ', this.learnwrong)
          if (this.learnwrong >= this.maxlearnwrong) {
            // 7 wrong, this is bad :( - popup and go to learn mode
            this.gotolearnmode = true
          }
        } else { // answered and true
          // to next question
          this.learncorrect++
          this.setNextQuestion()
        } 
      } else {
        // not answered -> next question
        this.learnunanswered++
        this.setNextQuestion()
      }

    } else if (this.query.qtyp === 'sc') {
      if (this.checkQueryScAnswered()) {
        this.gotolearnmode = false
        if (!this.checkQueryScAnsweredCorrect()) {
          // answered and false -> PopUp and prev question
          // learnwrong + 1 (max 7)
          this.learnwrong++
          // reset answers of current query
          this.query.qanswers.map(a => a.givenans = false)
          // back one query or to first query
          this.prevQuery()
          this.showWrongAnswerWarning = true;

          // 2 saniye sonra uyarı mesajını gizle
          setTimeout(() => {
            this.showWrongAnswerWarning = false;
          }, 1000);
          // this.refreshStats()
          console.log('Learn wrong: ', this.learnwrong)
          // this.refreshStats()
          console.log('Learn wrong: ', this.learnwrong)
          if (this.learnwrong >= this.maxlearnwrong) {
            // 7 wrong, this is bad :( - popup and go to learn mode
            this.gotolearnmode = true
          }
        } else { // answered and true
          // to next question
          this.learncorrect++
          this.setNextQuestion()
          // }
        }
      } else {
        // not answered -> next question
        this.learnunanswered++
        this.setNextQuestion()
      }
      // console.log('curr q: ', this.currentQnr)
    } else if (this.query.qtyp === 'fi') {
      if (this.checkQueryFiAnswered()) {
        this.gotolearnmode = false
        if (!this.checkQueryFiAnsweredCorrect()) {
          // answered and false -> PopUp and prev question
          // learnwrong + 1 (max 7)
          this.learnwrong++
          // reset answers of current query
          // this.query.qgiventxt = ''
          // back one query or to first query
          console.log('Learn wrong: ', this.learnwrong)
          this.prevQuery()
          this.showWrongAnswerWarning = true;

          // 2 saniye sonra uyarı mesajını gizle
          setTimeout(() => {
            this.showWrongAnswerWarning = false;
          }, 1000);
          // this.refreshStats()
          console.log('Learn wrong: ', this.learnwrong)
          // this.refreshStats()
          if (this.learnwrong >= this.maxlearnwrong) {
            // 7 wrong, this is bad :( - popup and go to learn mode
            this.gotolearnmode = true
          }
        } else { // answered and true
          // to next question
          this.learncorrect++
          this.setNextQuestion()
          // }
        }
      } else {
        // not answered -> next question
        this.learnunanswered++
        this.setNextQuestion()
      }

    }
//     this.refreshStats()

  }

  setNextQuestion() {
    if (this.currentQnr < this.ql101all.length - 1) {
      this.currentQnr++
      this.query = this.ql101all[this.currentQnr]
      console.log(this.currentQnr, this.query)
    }
    this.showanswers = false
    // this.refreshStats()
  }

  lastQuery() {
    this.currentQnr = this.ql101all.length - 1
    this.query = this.ql101all[this.currentQnr]
    this.showanswers = false
    // this.refreshStats()
  }


  toggleAnswers(qid: number): void {
    if (this.queryNrAnswersShow != qid) {
      this.queryNrAnswersShow = qid;
      this.showanswers = true
    } else {
      this.showanswers = !this.showanswers
    }
    // this.refreshStats()
  }

  toggleGivenAnswer(ansind: number) {
    this.query.qanswers[ansind].givenans = !this.query.qanswers[ansind].givenans
    // this.refreshStats()
  }

  changeGivenAnswer(ansind: number) {
    // first reset all radios
    this.query.qanswers.map(ans => ans.givenans = false)
    // second set new radio
    this.query.qanswers[ansind].givenans = !this.query.qanswers[ansind].givenans
    // this.refreshStats()
  }

  checkQueryMcAnswered() {
    // answered? (is one answer given 'true')
    if (this.query.qanswers.find(a => a.givenans === true)) {
      console.log('answered')
      return true
    } else {
      // not answered
      console.log('not answered')
      return false
    }
  }

  checkQueryMcAnsweredCorrect() {
    // correct answered? (are all givenans equal correct)
    if (this.query.qanswers.find(a => a.givenans != a.correct)) {
      // answered false
      console.log('answered false')
      return false
    } else {
      console.log('answered true')
      return true
    }
  }

  keyinput(myinput: string) {
    this.correctInput = false
    this.query.qgiventxt = myinput
    // check if correct
    if (this.query.qanswers.find(
      a => a.txt.find(t => t === this.query.qgiventxt))) {
      this.correctInput = true
    }
    console.log(this.correctInput, this.query.qgiventxt)
    // this.refreshStats()
  }

  checkQueryScAnswered() {
    // answered? (is one answer given 'true')
    if (this.query.qanswers.find(a => a.givenans === true)) {
      // console.log('answered')
      return true
    } else {
      // not answered
      // console.log('not answered')
      return false
    }
  }

  checkQueryScAnsweredCorrect() {
    // correct answered? (is givenans equal correct)
    if (this.query.qanswers.find(a => a.givenans === true && a.givenans === a.correct)) {
      // console.log('answered true')
      return true
    } else {
      // answered false
      // console.log('answered false')
      return false
    }
  }
  checkQueryFiAnswered() {
    // answered? (is one answer given 'true')
    if (this.query.qgiventxt != '') {
      console.log('answered')
      return true
    } else {
      // not answered
      console.log('not answered')
      return false
    }
  }

  checkQueryFiAnsweredCorrect() {
    // correct answered? qgiventxt in one of answers txt
    if (this.query.qanswers.find(q => q.txt.find(at => at === this.query.qgiventxt))) {
      console.log('answered true')
      return true
    } else {
      // answered false
      console.log('answered false')
      return false
    }
  }
  finishExam() {
    this.finish=true
  }
  calculateProgressWidth(): string {
    const progressPercentage = ((this.currentQnr + 1) / this.ql101all.length) * 100;
    return `${progressPercentage}%`;
  }

}
  <div class="alert alert-dark" role="alert">
    Check All Questions
    You have 7 Wrong Answers ==> {{learnwrong }}
  </div>

  <div *ngIf="control" class="options-container">
    <label for="questionCount">Select the number of questions:</label>


    <label for="questionType">Select the question type:</label>
    <select id="questionType" [(ngModel)]="selectedQuestionType">
      <option value="all">All</option>
      <option value="mc">Multiple Choice</option>
      <option value="sc">Single Choice</option>
      <option value="fi">Fill-In</option>
    </select>

    <button (click)="startQuiz()">Start Quiz</button>
  </div>

  <div class="progress">
    <ng-container *ngIf="ql101all.length > 0">
      <div class="progress-bar" role="progressbar" [style.width]="calculateProgressWidth()" 
           [attr.aria-valuenow]="currentQnr + 1" aria-valuemin="1" [attr.aria-valuemax]="ql101all.length"></div>
    </ng-container>
  </div>



  <div class="question-container" *ngIf="quizStarted">
    <div *ngIf="!this.finish" >
      <div *ngIf="!gotolearnmode"  >

      <button type="button" class="btn btn-outline-dark" (click)="firstQuery()">first</button>
      <button type="button" class="btn btn-outline-secondary" (click)="lastQuery()">last</button>


        <h5>Question:  {{ query.qid }} <br /></h5>

        <span *ngFor="let qtext of ql101all[currentQnr].qtxt; let i = index">
          {{ qtext }}<span *ngIf="i !== ql101all[currentQnr].qtxt.length - 1"> </span>
        </span>
        <!-- MC-->
        <div *ngIf="query.qtyp === 'mc'">
          <div *ngFor="let ans of query.qanswers; index as ansind">
            <span *ngIf="showanswers">
              <span *ngIf="ans.correct">
                <span *ngIf="queryNrAnswersShow === query.qid" class="boldgreen">
                  <input type="checkbox" (click)="ans.givenans = !ans.givenans" checked disabled />
                  {{ ansind + 1 }}: {{ ans.txt }} 
                  {{ ans.givenans }}
                </span>
              </span>
              <span *ngIf="!ans.correct">
                <span *ngIf="queryNrAnswersShow === query.qid">
                  <input type="checkbox" (click)="toggleGivenAnswer(ansind)" disabled />
                  {{ ansind + 1 }}: {{ ans.txt }}
                  {{ ans.givenans }}
                </span>
              </span>
              <span *ngIf="queryNrAnswersShow != query.qid">
                <input type="checkbox" (click)="toggleGivenAnswer(ansind)" disabled />
                {{ ansind + 1 }}: {{ ans.txt }} 
                {{ ans.givenans }}
              </span>
            </span>
            <span *ngIf="!showanswers">
              <div *ngIf="ans.givenans">
                <input type="checkbox" (click)="toggleGivenAnswer(ansind)" checked />
                {{ ansind + 1 }}: {{ ans.txt }} 
                {{ ans.givenans }}
              </div>
              <div *ngIf="!ans.givenans">
                <input type="checkbox" (click)="toggleGivenAnswer(ansind)" />
                {{ ansind + 1 }}: {{ ans.txt }} 
                {{ ans.givenans }}
              </div>
            </span>
          </div>
        </div>
        <!-- SC -- SC-->
        <div *ngIf="query.qtyp === 'sc'">
          <div *ngFor="let ans of query.qanswers; index as ansind">
            <span *ngIf="showanswers">
              <span *ngIf="ans.correct">
                <span *ngIf="queryNrAnswersShow === query.qid" class="boldgreen">
                  <input type="radio" (click)="ans.givenans = !ans.givenans" checked disabled />
                  {{ ansind + 1 }}: {{ ans.txt }} 
                  {{ ans.givenans }}
                </span>
              </span>
              <span *ngIf="!ans.correct">
                <span *ngIf="queryNrAnswersShow === query.qid">
                  <input type="radio" (click)="changeGivenAnswer(ansind)" disabled />
                  {{ ansind + 1 }}: {{ ans.txt }} 
                  {{ ans.givenans }}
                </span>
              </span>
              <span *ngIf="queryNrAnswersShow != query.qid">
                <input type="radio" (click)="changeGivenAnswer(ansind)" disabled />
                {{ ansind + 1 }}: {{ ans.txt }} 
                {{ ans.givenans }}
              </span>
            </span>
            <span *ngIf="!showanswers">
              <div *ngIf="ans.givenans">
                <input type="radio" (click)="changeGivenAnswer(ansind)" checked />
                {{ ansind + 1 }}: {{ ans.txt }} 
                {{ ans.givenans }}
              </div>
              <div *ngIf="!ans.givenans">
                <input type="radio" (click)="changeGivenAnswer(ansind)" />
                {{ ansind + 1 }}: {{ ans.txt }} 
                {{ ans.givenans }}
              </div>
            </span>
          </div>
        </div>
        <!-- FI - FillIn -- FI-->
        <div *ngIf="query.qtyp === 'fi'">
          <div *ngFor="let ans of query.qanswers; index as k">
            <span *ngIf="showanswers">
              <span *ngIf="queryNrAnswersShow === query.qid" class="boldgreen">
                {{ k + 1 }}: {{ ans.txt }}
              </span>
              <span *ngIf="queryNrAnswersShow != query.qid">
                {{ k + 1 }}:
                <input type="text" value="{{ ans.txt }}" />
              </span>
            </span>
            <div>
              <span *ngIf="correctInput">
                <span *ngIf="!showanswers">
                  {{ k + 1 }}:
                  <input type="text" #myinput (keyup)="keyinput(myinput.value)" value="{{ query.qgiventxt }}"
                    class="boldgreen"  (keyup.enter)="nextQuery()" /></span>
              </span>
              <span *ngIf="!correctInput">
                <span *ngIf="!showanswers">
                  {{ k + 1 }}:
                  <input type="text" #myinput (keyup)="keyinput(myinput.value)" value="{{ query.qgiventxt }}"
                     (keyup.enter)="nextQuery()" /></span>
              </span>
            </div>
          </div>

          <hr />
        </div>
        <div *ngIf="showWrongAnswerWarning===true" class="alert alert-danger d-flex align-items-center" role="alert">
          <svg class="bi flex-shrink-0 me-2" role="img" aria-label="Danger:"><use xlink:href="#exclamation-triangle-fill"/></svg>
          <div>
            Wrong answer! please try again...
          </div>
        </div>
      </div>
      <div *ngIf="gotolearnmode"  class="gotolearnmode">
       <h1>Correct Answer: {{this.learncorrect}}</h1>
       <h1>Wrong Answer:{{this.learnwrong}}</h1>
       <h1>Unanswer: {{this.learnunanswered}}</h1>
        <h2 class="dad">You had already 7 Wrong Answers</h2>
        <h5>Please go to LEARN mode</h5>

    <br>
    <button class="btn btn-primary" routerLink="/learn">I go to Learn Mode!</button>
      </div >

      <div class="butonlar">
        <button type="button" class="btn btn-outline-success" 
       (click)="prevQuery()">back</button>
      <button type="button" class="btn btn-outline-primary" 
       (click)="toggleAnswers(query.qid)">check</button>
      <button type="button" class="btn btn-outline-success" 
     (click)="nextQuery()">next</button>
      <button type="button" class="btn btn-outline-success" 
       (click)="refreshPage()">RESET</button>
      <button type="button" class="btn btn-outline-secondary" 
       (click)="finishExam()">Finish Exam</button>

      </div>

    </div>
    <br> <br>
  </div>




 <div *ngIf="this.finish" class="statistics">
  <h1>Correct Answer: {{this.learncorrect}}</h1>
  <h1>Wrong Answer:{{this.learnwrong}}</h1>
  <h1>Unanswer: {{this.learnunanswered}}</h1>
 </div>
