// Eksik olan importları ekleyin
import { Component, OnInit } from '@angular/core';
import { Qlpic101Service } from '../services/qlpic101.service';
import { LceQuery } from '../interfaces/lcequery';
import { Statistics } from '../interfaces/statistics';

@Component({
  selector: 'app-exam',
  templateUrl: './exam.component.html',
  styleUrls: ['./exam.component.css']
})
export class ExamComponent implements OnInit {
  ql101all: LceQuery[] = [];
  currentQuestion: number = -1;
  quizStarted: boolean = false;

  selectedQuestionCount: number = 1; // Varsayılan değeri isteğe göre ayarlayın
  selectedQuestionType: string = 'all'; // Varsayılan değeri isteğe göre ayarlayın
  correctAnswers: number = 0;
  wrongAnswers: number = 0;
  unanswered: number = 0;
  control: boolean=true;
  bitti :boolean=false;
  stats: Statistics

  constructor(private qs: Qlpic101Service) {
    // Servisten soruları alıp doldurun
    this.ql101all = this.qs.getallAll();
    this.stats = {
      qmaxnumber: 0,
      qanswered: 0,
      qwrong: 0,
      qcorrect: 0,
      qnotanswered: 0
    }
  }

  ngOnInit() {}
  nullStats() {
    this.stats.qmaxnumber = 0;
    this.stats.qanswered = 0;
    this.stats.qwrong = 0;
    this.stats.qcorrect = 0;
    this.stats.qnotanswered = 0;
  }
  resetStatsAll() {
    this.nullStats()
    return this.calcStatsAll();
  }
  calcStatsAll() {
    // numbers of questions
    this.stats.qmaxnumber = this.selectedQuestionCount
    // console.log('Anzahl Fragen: ', this.stats.qmaxnumber)
    // answered?
    let mcs =
      this.ql101all.filter(q => q.qtyp === 'mc').
        filter(q => q.qanswers.findIndex(a => a.givenans === true) > -1).length

    let scs =
      this.ql101all.filter(q => q.qtyp === 'sc')
        .filter(q => q.qanswers.findIndex(a => a.givenans === true) > -1).length

    let fis =
      this.ql101all.filter(q => q.qtyp === 'fi').filter(q => q.qgiventxt != '').length

    this.stats.qanswered = mcs + scs + fis
    console.log(mcs, scs, fis)

    // // not answered
    this.stats.qnotanswered = this.stats.qmaxnumber - mcs - scs - fis

    // // correct answered
    // mcs not correct
    let notcorrectmcs = this.ql101all.filter(q => q.qtyp === 'mc')
      .filter(q => q.qanswers.findIndex(a => a.correct != a.givenans) > -1).length
    console.log('mcs n c:', notcorrectmcs)
    // scs not correct
    let notcorrectscs = this.ql101all.filter(q => q.qtyp === 'sc')
      .filter(q => q.qanswers.findIndex(a => a.correct != a.givenans) > -1).length
    console.log('scs n c:', notcorrectscs)
    // fis not correct
    let notcorrectfis = this.ql101all.filter(q => q.qtyp === 'fi')
      .filter(q => q.qanswers.findIndex(a => a.txt.find(t => t === q.qgiventxt))).length
    console.log('fis n c:', notcorrectfis)

    this.stats.qcorrect = this.stats.qmaxnumber - notcorrectmcs - notcorrectscs - notcorrectfis
    // // wrong answered
    this.stats.qwrong = this.stats.qnotanswered - (notcorrectmcs + notcorrectscs + notcorrectfis)

    return this.stats;
  }

  startQuiz() {
    this.quizStarted = true;
    this.currentQuestion = 0;
    this.filterQuestions();
    this.nextQuestion();
  }

  nextQuestion() {
    if (this.currentQuestion < this.ql101all.length - 1) {
      this.currentQuestion++;
    }
    this.bittik()
  }

  filterQuestions() {
    if (this.selectedQuestionType === 'all') {
      this.ql101all = this.qs.getallAll()
    } else if(this.selectedQuestionType === 'mc'){
      this.ql101all = this.qs.getallMc()      
    }
    else if(this.selectedQuestionType === 'fi'){
      this.ql101all = this.qs.getallFi()
    }
    else if(this.selectedQuestionType === 'sc'){
      this.ql101all = this.qs.getallSc()
    }
    this.ql101all = this.ql101all.slice(0, this.selectedQuestionCount);
  }

  showAnswers() {
    this.resetStatsAll(); // İstatistikleri sıfırla ve yeniden hesapla

    for (let i = 0; i < this.selectedQuestionCount; i++) {
      const question = this.ql101all[i];

      if (question.qtyp === 'fi') {
        if (!question.qgiventxt) {
          this.stats.qnotanswered++;
        }
      } else {
        const selectedAnswers = question.qanswers.filter(ans => ans.givenans);

        if (selectedAnswers.length === 0) {
          this.stats.qnotanswered++;
        } else if (selectedAnswers.every(ans => ans.correct)) {
          this.stats.qcorrect++;
        } else {
          this.stats.qwrong++;
        }
      }
    }
  }






  bittik(){
    if(this.selectedQuestionCount==this.currentQuestion) {
      this.bitti = true
    }

  }







}
<div *ngIf="control" class="options-container">
  <label for="questionCount">Select the number of questions:</label>
  <input type="number" id="questionCount" [(ngModel)]="selectedQuestionCount" min="1" max="{{ ql101all.length }}">

  <label for="questionType">Select the question type:</label>
  <select id="questionType" [(ngModel)]="selectedQuestionType">
    <option value="all">All</option>
    <option value="mc">Multiple Choice</option>
    <option value="sc">Single Choice</option>
    <option value="fi">Fill-In</option>
  </select>

  <button (click)="startQuiz()">Start Quiz</button>
</div>

<div *ngIf="quizStarted && currentQuestion !== -1 && currentQuestion < selectedQuestionCount">
  <div class="question-container">
    <div class="question">
      <p>
        <span *ngFor="let qtext of ql101all[currentQuestion].qtxt; let i = index">
          {{ qtext }}<span *ngIf="i !== ql101all[currentQuestion].qtxt.length - 1"> </span>
        </span>
      </p>
    </div>
    <div class="answers" *ngIf="ql101all[currentQuestion].qtyp === 'mc' || ql101all[currentQuestion].qtyp === 'sc'">
      <ul>
        <li *ngFor="let answer of ql101all[currentQuestion].qanswers; let j = index">
          <label>
            <input type="{{ ql101all[currentQuestion].qtyp === 'mc' ? 'checkbox' : 'radio' }}"
              [(ngModel)]="answer.givenans" name="answerGroup">
            {{ answer.txt[0] }}
          </label>
        </li>
      </ul>
    </div>
    <div class="fill-in" *ngIf="ql101all[currentQuestion].qtyp === 'fi'">
      <input type="text" [(ngModel)]="ql101all[currentQuestion].qgiventxt" placeholder="Your answer">
    </div>
    <button class="next-button" (click)="nextQuestion()">Next Question</button>
  </div>
</div>

<div class="result-container" *ngIf="bitti">
  <h2>Quiz Completed!</h2>
  <p>Your results:</p>
  <p>Correct Answers: {{ stats.qcorrect }}</p>
  <p>Wrong Answers: {{ stats.qwrong }}</p>
  <p>Unanswered: {{ stats.qnotanswered }}</p>
  <p>Unanswered: {{ stats.qmaxnumber }}</p>
  <button (click)="showAnswers()">Show Answers</button>
</div>
