import { Component } from '@angular/core';
import { McQuery } from '../interfaces/mc-query';
import { Statistics } from '../interfaces/statistics';
import { Qlpic101Service } from '../services/qlpic101.service';
import { StatsService } from '../services/stats.service';

@Component({
  selector: 'app-exam',
  templateUrl: './exam.component.html',
  styleUrls: ['./exam.component.css']
})
export class ExamMcComponent {
  ql101mcall: McQuery[] = []

  showanswers = false
  queryNrAnswersShow = -1

  query: McQuery;
  currentQnr = -1

  statistic: Statistics;
  gotolearnmode: boolean

  // maxexamwrong in %
  maxexamwrong = 20
  // examwrong number of wrong questions
  examwrong: number

  examresult = false
  showResultQuestions = false

  constructor(
    private ql101Mc: Qlpic101Service,
    private stats: StatsService
  ) {
    this.ql101mcall = this.ql101Mc.getallMc()
    this.ql101Mc.initGivenAnswers()

    this.statistic = this.stats.calcStatsMc()

    this.currentQnr = 0
    this.query = this.ql101mcall[this.currentQnr]
    this.gotolearnmode = false
    this.examwrong = 0
  }


  resetStats() {
    this.statistic = this.stats.resetStatsMc()
    this.firstQuery()
    this.gotolearnmode = false
    this.examwrong = 0
  }

  resetAnswers() {
    // reset 'givenanswer's of all questions
    this.ql101mcall.map(q => q.qanswers.map(a => a.givenans = false))
    this.resetStats()
    this.firstQuery()
    this.examwrong = 0
  }

  refreshStats() {
    this.statistic = this.stats.calcStatsMc()
  }

  firstQuery() {
    this.currentQnr = 0
    this.query = this.ql101mcall[this.currentQnr]
    this.showanswers = false
    this.refreshStats()
  }

  prevQuery() {
    if (0 < this.currentQnr) {
      this.currentQnr--
      this.query = this.ql101mcall[this.currentQnr]
    }
    this.showanswers = false
    this.refreshStats()
  }

  nextQuery() {
    // ------------------------------------------------------
    // check if current question answered and correct -> next
    //   else update examwrong counter
    // answered?
    if (this.checkQueryMcAnswered()) {
      this.gotolearnmode = false
      if (!this.checkQueryMcAnsweredCorrect()) {
        // answered and false -> PopUp and prev question
        // examwrong + 1 (max 7)
        this.examwrong++
        // reset answers of current query - not in exam mode
        // this.query.qanswers.map(a => a.givenans = false)
        // back one query or to first query
        // this.prevQuery()
        this.refreshStats()
        console.log('Learn wrong: ', this.examwrong)
        console.log(this.examwrong,this.ql101mcall.length,this.maxexamwrong)
        if ((100 * (this.examwrong / this.ql101mcall.length)) > this.maxexamwrong) {
          // x% wrong, this is bad :( - popup and go to learn mode
          this.gotolearnmode = true
        }
        // this.setNextQuestion()
      } else { // answered and true
        // to next question
        // this.setNextQuestion()
        // }
      }
    } else {
      // not answered -> next question
    }
    this.setNextQuestion()
    // console.log('curr q: ', this.currentQnr)
  }

  setNextQuestion() {
    if (this.currentQnr < this.ql101mcall.length - 1) {
      this.currentQnr++
      this.query = this.ql101mcall[this.currentQnr]
      console.log(this.currentQnr, this.query)
    }
    this.showanswers = false
    this.refreshStats()
  }

  lastQuery() {
    this.currentQnr = this.ql101mcall.length - 1
    this.query = this.ql101mcall[this.currentQnr]
    this.showanswers = false
    this.refreshStats()
  }

  toggleAnswers(qid: number): void {
    if (this.queryNrAnswersShow != qid) {
      this.queryNrAnswersShow = qid;
      this.showanswers = true
    } else {
      this.showanswers = !this.showanswers
    }
    this.refreshStats()
  }

  toggleGivenAnswer(ansind: number) {
    this.query.qanswers[ansind].givenans = !this.query.qanswers[ansind].givenans
    this.refreshStats()
  }

  checkQueryMcAnswered() {
    // answered? (is one answer given 'true')
    if (this.query.qanswers.find(a => a.givenans === true)) {
      console.log('answered')
      return true
    } else {
      // not answered
      console.log('not answered')
      return false
    }
  }

  checkQueryMcAnsweredCorrect() {
    // correct answered? (are all givenans equal correct)
    if (this.query.qanswers.find(a => a.givenans != a.correct)) {
      // answered false
      console.log('answered false')
      return false
    } else {
      console.log('answered true')
      return true
    }
  }
  examEnd() {
    this.refreshStats()
    this.examresult = true
    this.showanswers = true
  }
}
<h5 style="text-align: center;">
  Exam Multiple Choise (MC) Questions <br>
</h5>
<div *ngIf="!examresult">
<div *ngIf="!gotolearnmode">

  <button type="button" class="btn btn-outline-dark" (click)="firstQuery()">first</button>
  <button type="button" class="btn btn-outline-secondary" style="margin-left: 80%
;" (click)="lastQuery()">last</button>

<br><br>
    <p>Question: {{ query.qid }} <br /></p>

    <div *ngFor="let qt of query.qtxt">
      <span *ngIf="qt[0] === ' '"> _ {{ qt }} <br /> </span>
      <span *ngIf="qt[0] != ' '"> {{ qt }} <br /> </span>
    </div>
    <!-- MC -->
    <div *ngIf="query.qtyp === 'mc'">
      <div *ngFor="let ans of query.qanswers; index as ansind">
        <span *ngIf="showanswers">
          <span *ngIf="ans.correct">
            <span *ngIf="queryNrAnswersShow === query.qid" class="boldgreen">
              <input
                type="checkbox"
                (click)="ans.givenans = !ans.givenans"
                checked
                disabled
              />
              {{ ansind + 1 }}: {{ ans.txt }} ++ {{ ans.correct }}:
              {{ ans.givenans }}
            </span>
          </span>
          <span *ngIf="!ans.correct">
            <span *ngIf="queryNrAnswersShow === query.qid">
              <input
                type="checkbox"
                (click)="toggleGivenAnswer(ansind)"
                disabled
              />
              {{ ansind + 1 }}: {{ ans.txt }} ++ {{ ans.correct }}:
              {{ ans.givenans }}
            </span>
          </span>
          <span *ngIf="queryNrAnswersShow != query.qid">
            <input
              type="checkbox"
              (click)="toggleGivenAnswer(ansind)"
              disabled
            />
            {{ ansind + 1 }}: {{ ans.txt }} ++ {{ ans.correct }}:
            {{ ans.givenans }}
          </span>
        </span>
        <span *ngIf="!showanswers">
          <div *ngIf="ans.givenans">
            <input
              type="checkbox"
              (click)="toggleGivenAnswer(ansind)"
              checked
            />
            {{ ansind + 1 }}: {{ ans.txt }} ++ {{ ans.correct }}:
            {{ ans.givenans }}
          </div>
          <div *ngIf="!ans.givenans">
            <input type="checkbox" (click)="toggleGivenAnswer(ansind)" />
            {{ ansind + 1 }}: {{ ans.txt }} ++ {{ ans.correct }}:
            {{ ans.givenans }}
          </div>
        </span>
      </div>
    </div> <br>
    <button type="button" class="btn btn-outline-success" style="margin-left: 30%
" (click)="prevQuery()">back</button>
<button type="button" class="btn btn-outline-success" style="margin-left: 20%
" (click)="nextQuery()">next</button> 
  <button type="button" class="btn btn-success" style="margin-left: 15%;" (click)="examEnd()">
  End Exam
  </button>
    <hr />
  </div>
  <div *ngIf="gotolearnmode">
    <h5 class="dad">You had already 7 Wrong Answers</h5>
    <h5>Please go to LEARN mode</h5>

  <br>
  <button class="btn btn-primary" routerLink="/learning">I go to Learn Mode!</button>
  </div>
</div>

<div *ngIf="examresult">
  <button class="btn btn-info" (click)="showResultQuestions = !showResultQuestions">Exam Questions
  </button>
  <button class="btn btn-primary" routerLink="/home" style="margin-left: 62%;">Home</button>
  <div class="statistics">
    <h2 style="text-align: center;">Your Result</h2>
    Number of Questions: {{ statistic.qmaxnumber }} <br />
    <ul>
      <li>You answered: {{ statistic.qanswered }} <br /></li>
      <ul>
        <li>Correct answered: {{ statistic.qcorrect }} <br /></li>
        <li>Wrong answered: {{ statistic.qwrong }} <br /></li>
      </ul>
      <li>Not answered: {{ statistic.qnotanswered }} <br /></li>
    </ul>
  </div>

  <!-- show exam question results correct/wrong -->
  <div *ngIf="showResultQuestions">
    <h3>Answered questions with correct/wrong answers!</h3>
    <div *ngFor="let query of ql101mcall">
      <p>Question: {{ query.qid }} | Typ: {{ query.qtyp }} <br /></p>

      <div *ngFor="let qt of query.qtxt">
        <span *ngIf="qt[0] === ' '"> _ {{ qt }} <br /> </span>
        <span *ngIf="qt[0] != ' '"> {{ qt }} <br /> </span>
      </div>
      <!-- MC -->
      <div *ngIf="query.qtyp === 'mc'">
        <div *ngFor="let ans of query.qanswers; index as ansind">
          <span *ngIf="showanswers">
            <span *ngIf="ans.correct && ans.givenans">
              <span  class="boldgreen">
                <input
                  type="checkbox"
                  checked
                  disabled
                />
                {{ ansind + 1 }}: {{ ans.txt }} ++ {{ ans.correct }}:
                {{ ans.givenans }}
              </span>
            </span>
            <span *ngIf="ans.correct && !ans.givenans">
              <span class="boldblue">
                <input
                  type="checkbox"
                  disabled
                  checked
                />
                {{ ansind + 1 }}: {{ ans.txt }} ++ {{ ans.correct }}:
                {{ ans.givenans }}
              </span>
            </span>

            <span *ngIf="!ans.correct && ans.givenans">
              <span class="boldred">
                <input
                  type="checkbox"
                  disabled
                />
                {{ ansind + 1 }}: {{ ans.txt }} ++ {{ ans.correct }}:
                {{ ans.givenans }}
              </span>
            </span>

            <span  *ngIf="!ans.correct && !ans.givenans">
              <input
                type="checkbox"
                disabled
              />
              {{ ansind + 1 }}: {{ ans.txt }} ++ {{ ans.correct }}:
              {{ ans.givenans }}
            </span>
          </span>
        </div>
      </div>      
    </div>
  </div>
</div>
